/* eslint-disable prefer-const */
import Link from 'next/link';
import Head from 'next/head';
import React from 'react';
import { render } from 'react-dom';
import MobileNavbar from './mobileNavbar';

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      closed: true,
      active: true,
    };
  }

  mobileNav() {
    this.setState((prevState) => ({ closed: !prevState.closed }));
  }

  hamburger() {
    this.setState((prevState) => ({ active: !prevState.active }));
  }

  render() {
    // eslint-disable-next-line react/destructuring-assignment
    let close = this.state.closed ? 'Navbarmobile' : ' Navbarmobileclose';
    // eslint-disable-next-line react/destructuring-assignment
    let active = this.state.active
      ? '"hamburger hamburger--collapse" is-active'
      : '"hamburger hamburger--collapse"';
    return (
      <>
        <Head>
          <meta
            name="viewport"
            content="width=device-width, initial-scale=1.0"
          />
        </Head>
        {/* Start-----Main container */}
        <div className="container-fluid my-4 ">
          <div className="row">
            <div className="col-4">
              {/* Start----Logo container */}
              <div className="nav-logo float-left">
                <Link href="/index">
                  <a>Logo</a>
                </Link>
              </div>
              {/* End----Logo container */}
            </div>
            <div className="col-8">
              {/* Start----Navabar buttons */}
              <div className="nav-buttons float-right d-none d-md-block">
                <ul className="list inline ">
                  <li className="list-inline-item pr-3">
                    <Link href="/About">
                      <a>About me</a>
                    </Link>
                  </li>
                  <li className="list-inline-item pr-3">
                    <Link href="/Contact">
                      <a>Contact</a>
                    </Link>
                  </li>
                  <li className="list-inline-item pr-3">
                    <Link href="/Portofolio">
                      <a>Portofolio</a>
                    </Link>
                  </li>
                  <li className="list-inline-item ">
                    <Link href="/Blog">
                      <a>Blog</a>
                    </Link>
                  </li>
                </ul>
              </div>
              {/* End----Navabar buttons */}
              {/* Start----Navbar button when collpase */}
              <div className="button  float-right d-block d-md-none">
                <button
                  label="hamburger"
                  type="button"
                  className=" btn shadow-none hamburger "
                  onClick={this.mobileNav.bind(this)}
                >
                  <div />
                  <div />
                  <div />
                </button>
              </div>
              {/* End----Navbar button when collapse */}
            </div>
          </div>
          {/* End----Main container */}
        </div>
        <div className="mobile">
          <div className={close} />
        </div>
        <button className={active} type="button">
          <span className="hamburger-box">
            <span className="hamburger-inner" />
          </span>
        </button>
        {/* <MobileNavbar /> */}
      </>
    );
  }
}

export default Header;
